// Calculate trends and predictions
const metrics = $input.all();

if (metrics.length < 7) {
  throw new Error('Dados insuficientes (mÃ­nimo 7 dias)');
}

// Calculate averages and trends
const calculateTrend = (data, key) => {
  const values = data.map(d => parseFloat(d.json[key]) || 0).filter(v => v > 0);
  if (values.length < 2) return { avg: 0, trend: 0 };

  const avg = values.reduce((a, b) => a + b, 0) / values.length;

  // Simple linear regression for trend
  const half = Math.floor(values.length / 2);
  const firstHalf = values.slice(0, half);
  const secondHalf = values.slice(half);

  const avgFirst = firstHalf.reduce((a, b) => a + b, 0) / firstHalf.length;
  const avgSecond = secondHalf.reduce((a, b) => a + b, 0) / secondHalf.length;

  const trend = ((avgSecond - avgFirst) / avgFirst) * 100; // % change

  return { avg: avg.toFixed(2), trend: trend.toFixed(1) };
};

const ctrAnalysis = calculateTrend(metrics, 'ctr');
const cpcAnalysis = calculateTrend(metrics, 'cpc');
const followersAnalysis = calculateTrend(metrics, 'new_followers');

// Predict next 7 days (simple projection)
const predictions = {
  ctr: {
    current_avg: ctrAnalysis.avg,
    trend: ctrAnalysis.trend,
    predicted_7d: (parseFloat(ctrAnalysis.avg) * (1 + parseFloat(ctrAnalysis.trend) / 100)).toFixed(2)
  },
  cpc: {
    current_avg: cpcAnalysis.avg,
    trend: cpcAnalysis.trend,
    predicted_7d: (parseFloat(cpcAnalysis.avg) * (1 + parseFloat(cpcAnalysis.trend) / 100)).toFixed(2)
  },
  followers: {
    current_avg: followersAnalysis.avg,
    trend: followersAnalysis.trend,
    predicted_7d: Math.round(parseFloat(followersAnalysis.avg) * (1 + parseFloat(followersAnalysis.trend) / 100))
  }
};

// Identify risks
const risks = [];
if (parseFloat(ctrAnalysis.trend) < -10) risks.push('CTR caindo (-' + Math.abs(ctrAnalysis.trend) + '%)');
if (parseFloat(cpcAnalysis.trend) > 15) risks.push('CPC aumentando (+' + cpcAnalysis.trend + '%)');
if (parseFloat(followersAnalysis.trend) < -20) risks.push('Crescimento desacelerando');

return [{
  json: {
    predictions: predictions,
    risks: risks,
    data_points: metrics.length,
    period: '30_days'
  }
}];

